                <style>
                    .fish__block.block-second {
                        flex-direction: column;
                    }

                    .fish__block .block-third__parag {
                        color: #000;
                        font-size: 18px;
                        font-weight: 400;
                        line-height: 150%;
                    }

                    .fish__block.block-third>div:last-child .block-third__parag {
                        color: var(--black, #00222A);
                        font-size: 36px;
                        font-weight: 700;
                        line-height: 150%;
                    }

                    .block-first__parag-b {
                        font-size: 18px;
                    }

                    .fish__block-box.top {
                        flex-direction: row;
                        gap: 40px;
                        align-items: flex-start;
                        justify-content: flex-start;
                    }

                    .fish__block-box.bottom {
                        flex-direction: row;
                        justify-content: right;
                    }

                    .block-second__img1-1 {
                        max-width: 401px;
                        max-height: 339px;
                    }

                    .fish__block-box.top .block-second__p {
                        max-width: 507px;
                        font-size: 18px;
                    }

                    .block-second__img1-2 {
                        max-width: 296px;
                        max-height: 307px;
                        position: relative;
                        right: 100px;
                    }


                    .block-second__img1-3 {
                        max-width: 632px;
                        max-height: 420px;
                        position: relative;
                        bottom: 100px;
                    }

                    .fish__block.block-third>div {
                        display: flex;
                        flex-direction: row;
                    }

                    .fish__block.block-third>div:first-child {
                        gap: 20%;
                    }

                    .fish__block.block-third>div:last-child {
                        align-items: flex-end;
                        top: -100px;
                        position: relative;
                    }


                    @media (max-width: 1100px) {
                        .block-first__flex {
                            gap: 24px;
                        }

                        .block-first {
                            gap: 24px;
                            flex-direction: row;
                        }

                        .block-first__parag {
                            padding: 30px;
                            font-size: 16px;
                            padding-right: 60px;
                        }

                        .fish__block-box.top .block-second__p {
                            max-width: auto;
                        }

                        .block-second__img1-1,
                        .block-second__img1-2 {
                            display: none;
                        }

                        .block-second__img1-3 {
                            bottom: 0;
                        }

                    }

                    @media (max-width: 950px) {
                        .block-first__flex {
                            flex-direction: column;
                        }

                        .block-first__parag {
                            padding: 24px;
                        }

                        .block-first__svg {
                            width: 35px;
                        }

                        .fish__block.block-third>div:last-child .block-third__parag {
                            font-size: 18px;
                        }

                        .fish__block.block-first {
                            flex-direction: column;
                        }


                        .block-first__flex {
                            flex-direction: column;
                        }

                        .container.fish__wrapper .block-first {
                            gap: 24px;
                            flex-direction: column;
                        }

                        .block-second {
                            margin: 40px 0;
                            align-items: center;
                            gap: 24px;
                        }

                        .block-third {
                            flex-direction: column;
                            gap: 24px;
                        }

                        .block-third__img {
                            margin: 24px 0;
                        }

                        .fish__block.block-third>div {
                            flex-direction: column;
                        }

                        .fish__block.block-third>div:last-child {
                            display: flex;
                            top: 0;
                            flex-direction: column-reverse;
                        }
                    }

                    @media (max-width: 800px) {
                        .block-first__flex {
                            flex-direction: column;
                        }

                        .block-first__parag {
                            padding: 24px;
                        }

                        .fish__block-box.top .block-second__p {
                            font-size: 16px;
                        }
                    }
