	<script>
		$(document).ready(function() {
			if ($('.news__card').length > 8) {
				$('.news__card:gt(8)').hide();
				$('.show-more').show();
			}

			$('.show-more').on('click', function() {
				if ($(this).text() === 'Show less') {
					$('.news__card:gt(8)').slideUp(500);
					$(this).text('Show more');
					$('html, body').animate({
						scrollTop: $(this).offset().top
					}, 500); // прокрутка к кнопке
				} else {
					if ($('.news__card:hidden').length === 0) {
						$(this).text('Show less');
					} else {
						$(this).text('Show more');
						$('.news__card:hidden').slice(0, 3).fadeIn(500);
						if ($('.news__card:hidden').length === 0) {
							$(this).text('Show less');
						}
						$('html, body').animate({
							scrollTop: $(this).offset().top
						}, 500); // прокрутка к кнопке
					}
				}
			});
		});
	</script>


	<script src="https://api-maps.yandex.ru/2.1/?lang=ru_RU" type="text/javascript"></script>

	<script type="text/javascript">
		ymaps.ready(init);

		function init() {

			var myMap = new ymaps.Map("map", {
					center: [42.050117, 41.731254],
					zoom: 16
				}, {
					searchControlProvider: 'yandex#search'
				}),
				myPlacemark = new ymaps.Placemark(myMap.getCenter(), {
					hintContent: 'geoaq',
					balloonContent: 'geoaq'
				}, {
					iconLayout: 'default#image',
					iconImageSize: [40, 40],
					iconImageHref: '',
					iconImageOffset: [-15, -25]
				});
			myMap.geoObjects
				.add(myPlacemark);
			myMap.behaviors
				.disable(['scrollZoom']);
			console.log(myMap);
		}
	</script>

